import {
  Body,
  Controller,
  HttpException,
  Post,
  UploadedFile,
  UseInterceptors,
} from '@nestjs/common';
import { FileInterceptor } from '@nestjs/platform-express';
import * as AWS from 'aws-sdk';
import * as process from 'process';
import { ApiBody, ApiConsumes, ApiOperation, ApiTags } from '@nestjs/swagger';
import { Express } from 'express';
import { webhook } from '../config.discord-webhook';

@ApiTags('Upload')
@Controller('upload')
export class UploadsController {
  @Post('file')
  @ApiOperation({
    summary: '파일 업로드',
    description:
      '`STUDENT`\n\n파일을 업로드합니다.`file` 필드에 파일을 첨부해주세요.\n\n해당 파일은 S3에 업로드됩니다.',
  })
  @UseInterceptors(FileInterceptor('file'))
  @ApiConsumes('multipart/form-data')
  @ApiBody({
    schema: {
      type: 'object',
      properties: {
        file: {
          type: 'file',
          format: 'binary',
        },
      },
    },
  })
  async uploadFile(@UploadedFile() file: Express.Multer.File) {
    try {
      AWS.config.update({
        region: process.env.AWS_REGION,
        credentials: {
          accessKeyId: process.env.AWS_ACCESS_KEY_ID,
          secretAccessKey: process.env.AWS_SECRET_ACCESS_KEY,
        },
      });
      return await new AWS.S3()
        .putObject({
          Key: `${Date.now() + file.originalname}`,
          Body: file.buffer,
          Bucket: process.env.AWS_S3_BUCKET_NAME,
        })
        .promise();
    } catch (error) {
      return error;
    }
  }

  @Post('base64Image')
  @ApiOperation({
    summary: 'base64 이미지 업로드',
    description:
      '`STUDENT`\n\nbase64로 변환된 이미지 데이터와 관련 형식을 업로드합니다.\n\n해당 이미지는 S3에 업로드됩니다.',
  })
  @ApiBody({
    schema: {
      type: 'string',
      example: {
        format: 'png',
        usage: 'profile',
        id: '1234567890',
        base64:
          '',
      },
    },
  })
  async uploadBase64(
    @Body('format') format: string,
    @Body('usage') usage: string,
    @Body('id') id: string,
    @Body('base64') base64: string,
  ) {
    const base64Data = Buffer.from(base64, 'base64');
    AWS.config.update({
      region: process.env.AWS_REGION,
      credentials: {
        accessKeyId: process.env.AWS_ACCESS_KEY_ID,
        secretAccessKey: process.env.AWS_SECRET_ACCESS_KEY,
      },
    });

    try {
      await webhook.info(`Uploading ${usage}/${id}.${format}`);
      return await new AWS.S3()
        .putObject({
          Key: `${usage}/${id}.${format}`,
          Body: base64Data,
          Bucket: process.env.AWS_S3_BUCKET_NAME,
        })
        .promise();
    } catch (error) {
      console.log(error);
      return HttpException;
    }
  }
}
